#include <bits/stdc++.h>
using namespace std;

int main()
{
    // Find the thirteen adjacent digits in the 1000-digit number that have the greatest product. What is the value of this product?
    string s =
        "";

        long long ans = 0;
    for (int i = 0; i <= s.size() - 13; i++)
    {
        long long prod = 1;
        for (int j = 0; j < 13; j++)
        {
            prod *= (s[i + j] - '0');
        }
        ans = max(ans, prod);
    }
    cout << ans << "\n";
    return 0;
}